INSERT INTO ingresso_inf (id, informacao, localidade, programacao, duracao, id_ingresso)
				VALUES (3, 'A Experiência Krug é uma visita na fábrica onde o cliente participa de um tour guiado, passando pela história e produção das cervejas Krug Bier. O participante ainda ganha um Kit Experiência Krug que contém uma taça exclusiva para a degustação de alguns estilos de cerveja.', 'Av. Montreal, 280 - Jardim Canada, Nova Lima - MG, 34007-720, Brasil', 'A visita tem a duração média de 1 hora e ao final, o participante ainda poderá fazer compras em nossa loja da fábrica que tem uma variedade de produtos exclusivos além de chopes e cervejas fresquinhos!', '05:00', 3);
				
INSERT INTO ingresso_inf (id, informacao, localidade, programacao, duracao, id_ingresso)
				VALUES (4, 'O maior festival de música que São Paulo já viu, vai acontecer em setembro de 2023 em interlagos. Dos mesmos criadores de Rock in Rio, o The Town promete estremecer uma das maiores cidades do mundo, com cinco palcos, sete grandes espaços.', 'Autódromo de Interlagos', 'Com previsão para o The Town 2023 é para os dias 2, 3, 8, 9 e 10 de setembro de 2023.', '08:00', 4);
				
